mapper.select:
SELECT COUNT(*) 
FROM user 
WHERE USER_ID = #{USER_ID};

SELECT COUNT(*) 
FROM user 
WHERE USER_MAIL = #{USER_MAIL}

AND USER_ID != #{USER_ID}
;

SELECT COUNT(*) 
FROM user 
WHERE USER_NICKNAME = #{USER_NICKNAME}

AND USER_ID != #{USER_ID}
;

SELECT *  
FROM user 
WHERE USER_ID = #{USER_ID}
AND USER_PASSWORD = #{USER_PASSWORD};
